function balanceUpdate()
{
	ajaxLoad({id: '#', url: '/bitrix/components/payeer/account.info2/templates/top2/ajax.php?action=balance2&sessid=a41900747029ec8a7cabe99df04afef4', afterSuccess: 'afterBalanceUpdate'}, {nprogress: 'N', type: 'null', formType: 'json'});
}

function afterBalanceUpdate(e, data)
{
   //pre(['afterBalanceUpdate', data]);

   if (data != '')
	{
      itogBalance = data;

      var itogSum = 0;

      if (data.balance != '' && data.balance != undefined)
      {
         for (var curr in data.balance)
         {
            //pre([curr, data.balance[curr]]);

            var ar = data.balance[curr];

            var n = Number(ar[0].replace(/[\s,]+/g, '') + '.' + ar[1]);

            if (isNaN(n))
            {
                n = 0;
            }

            
            itogSum += Math.ceil10(arInternalExchangeRate[curr + '/' + itogCurr] * n, -arCurrPr[itogCurr]);

            $('.account_balance .curr-' + curr + ' span.int').html(ar['0']);
            $('.account_balance .curr-' + curr + ' span.pr').html('.' + ar['1']);

            $('.header .wallet .balance .balance-curr--' + curr.toLowerCase() + ' s').html(ar['0'] + '<sup>.' + ar['1'] +'</sup>');

            $('.div-balance-update .balance-item--' + curr.toLowerCase() + ' .n-select__selected-value-caption').html(ar['0'] + '.' + ar['1']);
         }

         if ($('.trade-management').length)
         {
            var arPair = $('#id_trade_pair').val().split('_');
            //pre(data.balance[arPair[0]]);

            var sumForSell = data.balance[arPair[0]].join('.').replace(/[^0-9\.]+/g, '');
            var sumForBuy = data.balance[arPair[1]].join('.').replace(/[^0-9\.]+/g, '');

            $('#id_trade_balance_for_sell').val(sumForSell);
            $('#id_trade_balance_for_sell').closest('.trade-management__balance-sum').find('div').html(tradeAmountFormat(sumForSell));

            $('#id_trade_balance_for_buy').val(sumForBuy);
            $('#id_trade_balance_for_buy').closest('.trade-management__balance-sum').find('div').html(tradeAmountFormat(sumForBuy));
         }
      }

      if (data.balancet != '' && data.balancet != undefined)
      {
         var bShowActiveOrdersTitle = false;

         for (var curr in data.balancet)
         {
            var ar = data.balancet[curr];
            //pre([curr, ar]);

            var n = Number(ar[0].replace(/[\s,]+/g, '') + '.' + ar[1]);

            if (isNaN(n))
            {
                n = 0;
            }

            itogSum += Math.ceil10(arInternalExchangeRate[curr + '/' + itogCurr] * n, -arCurrPr[itogCurr]);

            $('.account_balance .trade-curr-' + curr + ' span.int').html(ar['0']);
            $('.account_balance .trade-curr-' + curr + ' span.pr').html('.' + ar['1']);

            $('.header .wallet .balance .balance-trade-curr--' + curr.toLowerCase() + ' s').html(ar['0'] + '<sup>.' + ar['1'] +'</sup>');

            if (n > 0)
            {
               bShowActiveOrdersTitle = true;

               $('.header .wallet .balance .balance-trade-curr--' + curr.toLowerCase()).show();

               $('.account_balance .trade-curr-' + curr).removeClass('balance-0');

               if ($('.abn-h2 span.active').data('type') == 'active-orders')
               {
                  $('.account_balance .trade-curr-' + curr).show();
               }
            }
            else
            {
               $('.header .wallet .balance .balance-trade-curr--' + curr.toLowerCase()).hide();

               $('.account_balance .trade-curr-' + curr).removeClass('balance-0').addClass('balance-0');

               if ($('.abn-h2 span.active').data('type') == 'active-orders')
               {
                  $('.account_balance .trade-curr-' + curr).hide();
               }
            }
         }

         if (bShowActiveOrdersTitle)
         {
            $('.balance-title-active-orders').show();
         }
         else
         {
            $('.balance-title-active-orders').hide();

            if ($('.main_abalance').length)
            {
               $('.account_balance_new > li').hide();
         		$('.account_balance_new > li.all').not('.balance-0').show();

         		$('.abn-h2 span.active').removeClass('active');
         		$('.main_abalance span[data-type="all"]').addClass('active');
            }
         }
      }

      
      itogSum = number_format(itogSum, arCurrPr[itogCurr], '.', ',');
      //pre('itogSum = ' + itogSum);

      arItog = itogSum.split('.');

      $('.account_balance .itog span.int').html(arItog['0']);
      $('.account_balance .itog span.pr').html('.' + arItog['1']);

      $('.account_balance .itog').attr('class', '').addClass('itog itog-' + itogCurr);
      $('.account_balance li.itog .val .curr').attr('class', '').addClass('curr fa fa-' + itogCurr.toLowerCase());

      $('.header-total .icon-padding-top').attr('class', '').addClass('icon-padding-top icon-' + itogCurr.toLowerCase());
      $('.header-total .btn--amount s').html(arItog['0'] + '<sup>.' + arItog['1'] + '</sup>');

      $('.wallet-total .icon-padding-top').attr('class', '').addClass('icon-padding-top icon-' + itogCurr.toLowerCase());
      $('.wallet .wallet-total s').html(arItog['0'] + '<sup>.' + arItog['1'] + '</sup>');

      	}
	else
	{
		window.location.reload();
	}
}

