		return false;
	});

	$('.link-curr').click(function(){
		var curr = $(this).data('curr');
		if(curr == 'all') {
			$('#tab-myoperations a.show_filter').show();
		} else {
			$('#tab-myoperations a.show_filter').hide();
		}
		$('#tab-myoperations a.hide_filter').hide();

		$('#tab-myoperations div.filter').slideUp();

		$('#tab-myoperations .filter form select[name=curr]').val($(this).data('curr'));
